{
  "name": "BISA Soccer Class Matcher",
  "description": "Premium youth soccer training class matcher and scheduling assistant using AI to find the perfect trial class."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface MatcherResponse {
  status: 'success' | 'missing_info' | 'not_eligible';
  identified_age: number | null;
  recommended_class_name: string | null;
  recommended_options: string[];
  reply_message_en: string;
}

export interface ChatMessage {
  role: 'user' | 'assistant';
  content: string | MatcherResponse;
}

export interface ClassSchedule {
  ageRange: string;
  time: string;
  day: string;
}

import { GoogleGenAI, Type } from "@google/genai";
import { MatcherResponse } from "../types";

// Define the system instruction as a constant to ensure consistency
const SYSTEM_INSTRUCTION = `
You are the "Class Matcher AI" for the Soccer Academy (BISA). Your role is to receive information about a child (age and location) and find the perfect class in our schedule.

**YOUR PERSONALITY:**
- Objective, polite, and American.
- NEVER invent schedules. Use ONLY the database below.

**DATABASE (SCHEDULE):**
LOCATION & CONTACT:
- Address: Sangaree Middle School Gym, 1050 Discovery Dr. Ladson, SC 29456
- Phone/WhatsApp: +1 (843) 304-9414

**BISA Winter Futsal Program ðŸ”¥ Trial Class Scheduling (Wednesdays ONLY):**
- Age 4 to 8: 6:00 PM - 7:00 PM
- Age 9 to 15: 8:15 PM - 9:15 PM

**LOGIC RULES:**
1. If the child's age is not served (e.g., 2, 3, 16, 17+), return status "not_eligible".
2. If information is missing (user provided age but not location, or vice versa), return status "missing_info".
   - Note: If the user mentions a location near Ladson, SC (like Charleston, Summerville, Goose Creek), consider it valid. If they are very far away (e.g. New York, California), warn them in the reply message but you can still show the class if they insist, or mark as not_eligible if you deem it impossible. However, strictly follow the prompt: "missing_info" if they simply didn't say where they are.
3. If valid, return "success" with the specific Wednesday time slot.

**OUTPUT FORMAT:**
Return ONLY valid JSON.
`;

export const findClassMatch = async (userInput: string): Promise<MatcherResponse> => {
  try {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: userInput,
      config: {
        systemInstruction: SYSTEM_INSTRUCTION,
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            status: { type: Type.STRING, enum: ['success', 'missing_info', 'not_eligible'] },
            identified_age: { type: Type.NUMBER, nullable: true },
            recommended_class_name: { type: Type.STRING, nullable: true },
            recommended_options: { type: Type.ARRAY, items: { type: Type.STRING } },
            reply_message_en: { type: Type.STRING }
          },
          required: ['status', 'reply_message_en', 'recommended_options'],
        },
      },
    });

    const text = response.text;
    if (!text) throw new Error("No response from AI");

    return JSON.parse(text) as MatcherResponse;
  } catch (error) {
    console.error("Error matching class:", error);
    // Fallback error response
    return {
      status: 'missing_info',
      identified_age: null,
      recommended_class_name: null,
      recommended_options: [],
      reply_message_en: "I'm having trouble connecting to the schedule right now. Please try again or contact us directly at +1 (843) 304-9414."
    };
  }
};

