import React, { useState } from 'react';
import { MatcherResponse } from '../types';
import { findClassMatch } from '../services/geminiService';

interface MatcherFormProps {
  onResult: (result: MatcherResponse) => void;
}

const MatcherForm: React.FC<MatcherFormProps> = ({ onResult }) => {
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const result = await findClassMatch(input);
      onResult(result);
      if (result.status === 'success') {
        setInput(''); 
      }
    } catch (err) {
      setError("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-w-3xl mx-auto -mt-10 relative z-20 px-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 md:p-8 border border-gray-100">
        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold text-gray-900 font-display">Find Your Class</h2>
          <p className="text-gray-500 mt-2">
            Tell our AI Assistant about your child (Age & Location) to check availability immediately.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="relative">
          <div className="relative">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="e.g., 'My son is 7 years old and we live in Ladson, SC'"
              className="w-full h-32 p-4 pr-12 text-lg text-gray-900 placeholder-gray-400 bg-gray-50 border-2 border-gray-200 rounded-xl focus:border-bisa-navy focus:ring-0 focus:bg-white transition-all resize-none"
              disabled={loading}
            />
            <div className="absolute bottom-4 right-4 text-xs text-gray-400">
              AI Powered
            </div>
          </div>

          {error && (
            <p className="text-red-500 text-sm mt-2">{error}</p>
          )}

          <button
            type="submit"
            disabled={loading || !input.trim()}
            className={`mt-4 w-full py-4 rounded-xl text-white font-bold text-lg shadow-lg transition-all duration-300 flex items-center justify-center space-x-2
              ${loading || !input.trim() 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-bisa-navy hover:bg-blue-900 hover:shadow-xl hover:-translate-y-0.5'
              }`}
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                <span>Analyzing Schedule...</span>
              </>
            ) : (
              <span>Check Availability</span>
            )}
          </button>
        </form>

        <div className="mt-6 flex items-center justify-center space-x-6 text-sm text-gray-500">
          <div className="flex items-center">
            <svg className="w-4 h-4 mr-1.5 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>
            Instant Response
          </div>
          <div className="flex items-center">
            <svg className="w-4 h-4 mr-1.5 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>
            Current Winter Schedule
          </div>
        </div>
      </div>
    </div>
  );
};

export default MatcherForm;

import React from 'react';
import { MatcherResponse } from '../types';

interface ResultsModalProps {
  result: MatcherResponse | null;
  onClose: () => void;
}

const ResultsModal: React.FC<ResultsModalProps> = ({ result, onClose }) => {
  if (!result) return null;

  const isSuccess = result.status === 'success';
  const isMissing = result.status === 'missing_info';

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
      <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">

        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 transition-opacity" aria-hidden="true" onClick={onClose}></div>

        <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>

        <div className="inline-block align-bottom bg-white rounded-2xl text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg w-full">

          <div className="px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
            <div className="sm:flex sm:items-start">

              <div className={`mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full sm:mx-0 sm:h-10 sm:w-10 ${
                isSuccess ? 'bg-green-100' : isMissing ? 'bg-yellow-100' : 'bg-red-100'
              }`}>
                {isSuccess ? (
                  <svg className="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                  </svg>
                ) : isMissing ? (
