                      <div className="text-xl font-bold text-bisa-navy">{result.recommended_class_name}</div>

                      <div className="mt-3 space-y-2">
                         {result.recommended_options.map((opt, idx) => (
                           <div key={idx} className="flex items-center text-sm font-medium text-gray-700">
                             <svg className="w-4 h-4 mr-2 text-bisa-gold" fill="none" viewBox="0 0 24 24" stroke="currentColor">
