import { GoogleGenAI, Type } from "@google/genai";
import { VoucherDetails } from "../types";

const SYSTEM_INSTRUCTION = `
You are the "Class Matcher AI" for BISA Soccer Academy.
Your goal is to validate if a child is eligible for our Winter Futsal Program based on their age and location.

**SCHEDULE DATABASE:**
- Address: Sangaree Middle School Gym, 1050 Discovery Dr. Ladson, SC 29456
- Schedule (Wednesdays ONLY):
  - Age 4 to 8: 6:00 PM - 7:00 PM
  - Age 9 to 15: 8:15 PM - 9:15 PM

**RULES:**
1. **Age Check**:
   - If Age is 4-8: Return eligible, time 6-7 PM.
   - If Age is 9-15: Return eligible, time 8:15-9:15 PM.
   - If Age < 4 or > 15: Return status "not_eligible".
2. **Location Check**:
   - If the location is reasonably close to Ladson, SC (e.g., Summerville, Charleston, Goose Creek, North Charleston), allow it.
   - If the location is very far (e.g., different state, or > 1 hour drive), you can still mark as eligible but include a warning message, or mark not_eligible if it's absurdly far (like another country). Generally, accept SC locations.

**OUTPUT JSON:**
Return a JSON object matching the Schema.
`;

export const findClassMatch = async (age: string, location: string): Promise<VoucherDetails> => {
  try {
    const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: `Child Age: ${age}, Location: ${location}`,
      config: {
        systemInstruction: SYSTEM_INSTRUCTION,
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            status: { type: Type.STRING, enum: ['eligible', 'not_eligible'] },
            ageGroup: { type: Type.STRING },
            timeSlot: { type: Type.STRING },
            location: { type: Type.STRING },
            day: { type: Type.STRING },
            message: { type: Type.STRING }
          },
          required: ['status', 'ageGroup', 'timeSlot', 'location', 'day'],
        },
      },
    });

    const text = response.text;
    if (!text) throw new Error("No response from AI");

    return JSON.parse(text) as VoucherDetails;
  } catch (error) {
    console.error("AI Service Error:", error);
    // Fallback logic if AI fails
    const numAge = parseInt(age);
    if (!isNaN(numAge)) {
      if (numAge >= 4 && numAge <= 8) {
        return {
          status: 'eligible',
          ageGroup: '4 - 8 Years Old',
          timeSlot: '6:00 PM - 7:00 PM',
          day: 'Wednesday',
          location: 'Sangaree Middle School Gym',
          message: 'AI Service unavailable, falling back to basic schedule.'
        };
      } else if (numAge >= 9 && numAge <= 15) {
        return {
          status: 'eligible',
          ageGroup: '9 - 15 Years Old',
          timeSlot: '8:15 PM - 9:15 PM',
          day: 'Wednesday',
          location: 'Sangaree Middle School Gym',
          message: 'AI Service unavailable, falling back to basic schedule.'
        };
      }
    }

    return {
      status: 'not_eligible',
      ageGroup: '',
      timeSlot: '',
      location: '',
      day: '',
      message: 'Unable to determine eligibility.'
    };
  }
};

